const defaultTheme="light";function getCurrentTheme(){return localStorage.getItem("theme")||defaultTheme}function setTheme(){const e=getCurrentTheme(),t=document.body,o=document.querySelector("#modeSwitcher .bi-moon"),n=document.querySelector("#modeSwitcher .bi-sun");t.classList.toggle("dark-theme","dark"===e),"dark"===e?(o.style.display="none",n.style.display="inline-block"):(o.style.display="inline-block",n.style.display="none")}function toggleTheme(){const e=getCurrentTheme(),t=document.body,o=document.querySelector("#modeSwitcher .bi-moon"),n=document.querySelector("#modeSwitcher .bi-sun"),l="dark"===e?"light":"dark";localStorage.setItem("theme",l),t.classList.toggle("dark-theme"),t.classList.contains("dark-theme")?(o.style.display="none",n.style.display="inline-block"):(o.style.display="inline-block",n.style.display="none")}setTheme();const modeSwitcher=document.getElementById("modeSwitcher");modeSwitcher.addEventListener("click",toggleTheme);